import React from "react";
import { Container, Row, Col, ProgressBar } from "react-bootstrap";

const Skills = () => {
  return (
    <>
      <Container>
        <h1 className="text-center mt-5">Mis Habilidades</h1>
        <Row className="mt-5 neon-border m-2">
          <Col>
            <h2>Metodologías de trabajo</h2>
            <ProgressBarData tittle="SCRUM" per={80} />
            <ProgressBarData tittle="RUP" per={80} />
          </Col>
        </Row>
        <Row className="mt-5 m-2">
          <Col className="m-2">
            <h2>Lenguajes de programación</h2>
            <ContentIcon icon="fa-brands fa-java fa-2x p-1" detail="JAVA" per={40} level="Intermedio" />
            <ContentIcon icon="fa-brands fa-java fa-2x p-1" detail="JAVA" per={40} level="Intermedio" />
             <ContentIcon icon="fa-brands fa-java fa-2x p-1" detail="JAVA" per={40} level="Intermedio" />
          </Col>
        </Row>
        <Row className="mt-5 neon-border m-2">
          <Col>
            <h2>Otras Habilidades</h2>
          </Col>
        </Row>
      </Container>
    </>
  );
};

const ProgressBarData = ({ tittle, per }) => {
  return (
    <div className="p-2">
      <div className="text-start">{tittle}:</div>
      <div className="text-end">
        <i className="float-right">{per}%</i>
      </div>
      <ProgressBar now={per} label={`${per}%`} visuallyHidden />
    </div>
  );
};

const ContentIcon = ({ icon, detail, per, level }) => {
  const iconColor = per >= 50 ? "text-primary" : ""; // Cambiar a color azul si el porcentaje es mayor o igual a 50
  return (
    <div className={`hobbie hobby-item neon-border ${iconColor}`} style={{ display: "inline-block", padding: "10px" }}>
      <div className="hobby-icon"><i className={icon}></i></div>
      <div className="hobby-text">{detail}</div>
      <div>{level}</div>
    </div>
  );
};

export default Skills;
