import React, { useState } from "react";
import { Container, Row, Col, Button, Modal } from "react-bootstrap";

const Timeline = () => {
  return (
    <Container>
      <Row className="timeline-container">
        <Col xs={12} lg={6}>
          <ButtonModal
            title="EDUCACIÓN"
            columna1={[
              {
                year: "2019 - En curso",
                title: "UNIVERSIDAD TECNOLÓGICA DEL PERÚ (UTP)",
                desc: "Actualmente me encuentro cursando la carrera de Ingeniería de Software, estoy en 9no semestre y mi fecha de egreso aproximado es en Diciembre del 2023",
                downloadUrl: "../../PDF/CV-JC.pdf",
              },
              {
                year: "2007",
                title: "CISCO NETWORKING ACADEMY",
                desc: "adsada",
                downloadUrl: "URL_DEL_ARCHIVO_PDF2",
              },
            ]}
            columna2={[
              {
                year: "2010",
                title: "PYTHON INSTITUTE",
                desc: "adsada",
                downloadUrl: "URL_DEL_ARCHIVO_PDF3",
              },
            ]}
          />
        </Col>
        <Col xs={12} lg={6}>
          <ButtonModal
            title="CURSOS - SEMINARIOS"
            columna1={[
              {
                year: "2007",
                title: "ORACLE",
                desc: "adsada",
                downloadUrl: "URL_DEL_ARCHIVO_PDF4",
              },
              {
                year: "2010",
                title: "EXCEL UTP",
                desc: "adsada",
                downloadUrl: "URL_DEL_ARCHIVO_PDF5",
              },
            ]}
            columna2={[
              {
                year: "2010",
                title: "APP UTP",
                desc: "adsada",
                downloadUrl: "URL_DEL_ARCHIVO_PDF6",
              },
              {
                year: "2010",
                title: "INGLES UTP",
                desc: "adsada",
                downloadUrl: "URL_DEL_ARCHIVO_PDF7",
              },
            ]}
          />
        </Col>
      </Row>
    </Container>
  );
};

const ButtonModal = ({ title, columna1, columna2 }) => {
  const [showModal, setShowModal] = useState(false);
  const [selectedContent, setSelectedContent] = useState(null);

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleOpenModal = (content) => {
    setSelectedContent(content);
    setShowModal(true);
  };
  
  const getFileNameFromUrl = (url) => {
    const urlParts = url.split("/");
    return urlParts[urlParts.length - 1];
  };
  

  return (
    <>
      <h2>{title}</h2>
      <Button
        variant="primary"
        onClick={() => handleOpenModal(columna1.concat(columna2))}
      >
        Ver detalles
      </Button>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        dialogClassName="custom-modal modal-90w"
        aria-labelledby="example-modal-sizes-title-lg"
        centered
        size="xl"
      >
        <Modal.Header>
          <Modal.Title>{title}</Modal.Title>
          <button className="custom-close-button" onClick={handleCloseModal}>
            &times;
          </button>
        </Modal.Header>

        <Modal.Body>
          {selectedContent && (
            <Container>
              <Row>
                <Col xs={12} md={6}>
                  <div className="timeline">
                    {columna1.map((item, index) => (
                      <div key={index} className="timeline-item">
                        <div className="timeline-dot"></div>
                        <div className="timeline-content">
                        <div className="timeline-balloon">
  <h3 className="">{item.title}</h3>
  <p>{item.desc}</p>
  <div className="timeline-year">{item.year}</div>
  <a
  href={selectedContent && selectedContent.downloadUrl}
  className="btn btn-outline-info my-2"
  target="_blank"
  rel="noopener noreferrer"
  download={
    selectedContent && getFileNameFromUrl(selectedContent.downloadUrl)
  }
>
  Descargar
</a>

</div>

                        </div>
                      </div>
                    ))}
                  </div>
                </Col>
                <Col xs={12} md={6}>
                  <div className="timeline">
                    {columna2.map((item, index) => (
                      <div key={index} className="timeline-item">
                        <div className="timeline-dot"></div>
                        <div className="timeline-content">
                          <div className="timeline-balloon">
                            <h3 className="">{item.title}</h3>
                            <p>{item.desc}</p>
                            <div className="timeline-year">{item.year}</div>
                            <a href={item.downloadUrl} className="btn btn-outline-info my-2" target="_blank" rel="noopener noreferrer">
                              Descargar
                            </a>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </Col>
              </Row>
            </Container>
          )}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default Timeline;
