import React, { useState } from "react";
import { Nav, Image, Container, Navbar } from "react-bootstrap";

const NavbarHeader = () => {
  const [activeItem, setActiveItem] = useState(0);
  const [isHovered, setIsHovered] = useState(false);

  const handleItemClick = (index) => {
    setActiveItem(index);
  };

  return (
    <Navbar expand="lg" data-bs-theme="dark">
      <Container>
        <Navbar.Brand>
          <Image
            src="https://avatars.githubusercontent.com/u/133301187?v=4"
            roundedCircle
            width={50}
            height={50}
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mx-auto custom-nav">
            <Nav.Link>
              <ContentList
                icon="fa-solid fa-user"
                detail="ABOUT ME"
                isActive={activeItem === 0}
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
                onClick={() => handleItemClick(0)}
              />
            </Nav.Link>
            <Nav.Link>
              <ContentList
                icon="fa-solid fa-code"
                detail="SKILLS"
                isActive={activeItem === 1}
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
                onClick={() => handleItemClick(1)}
              />
            </Nav.Link>
            {/* Agregar otros elementos de la lista aqu√≠ */}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

const ContentList = ({ icon, detail, isActive, onMouseEnter, onMouseLeave, onClick }) => {
  return (
    <>
      <div
        className={`info-icon text-center ${isActive ? "active" : ""}`}
        onMouseEnter={onMouseEnter}
        onMouseLeave={onMouseLeave}
        onClick={onClick}
      >
        <i className={icon}></i> {detail}
      </div>
      {isActive && isHovered && <div className="content-highlight"></div>}
    </>
  );
};

export default NavbarHeader;
