import React from "react";
import Img1 from "../../img/fondo1.jpg"
import { Container, Row, Col, Card } from "react-bootstrap";

const ProjectsContent = () => {
  return (
    <>
      <div className="title-subtititel my-3">
        <h2>Mis proyectos</h2>
      </div>
      <Container className="p-4 my-4">
        <Row>
          {renderCard(
            "SISTEMA WEB - FASTAGE",
            "Realizado usando Netbeans 8.1, simula un sistema de ventas básico con facturas en PDF",
            Img1,
            1
          )}
          {renderCard(
            "Proyecto 2",
            "Descripción del proyecto 2",
            "ruta-de-la-imagen-2.jpg",
            2
          )}
        </Row>
        <Row>{/* Puedes agregar más filas de tarjetas aquí */}</Row>
      </Container>
    </>
  );
};
const renderCard = (title, text, imageUrl, cardKey) => {
  return (
    <Col key={cardKey} xs={12} lg={6} className="p-2">
      <Card className="bg-dark text-white">
        <Card.Img src={imageUrl} alt="Card image" />
        <Card.ImgOverlay>
          <div className="card-content">
            <Card.Title>{title}</Card.Title>
            <Card.Text>{text}</Card.Text>
            <Card.Text>Last updated 3 mins ago</Card.Text>
          </div>
         
          <a href="link-de-youtube" target="_blank" rel="noopener noreferrer">
            <i className="fa-brands fa-youtube"></i>
          </a>
 
        </Card.ImgOverlay>
      </Card>
    </Col>
  );
};

export default ProjectsContent;
