import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import logo from "../../img/fondo5.jpg";

const AboutMe = () => {
  return (
    <>
    <Container>
      <Row className="justify-content-md-center p-5" xs={1} md={1} sm={1} lg={2}>
        <Col xs={12} lg={6} className="d-flex flex-column justify-content-start align-items-start">
          <Image src="https://avatars.githubusercontent.com/u/133301187?v=4" fluid rounded width={400}
              height={400}/>
        </Col>
        <Col xs={12} lg={6}>
          <div className="p-4">
            <h2>Sobre mí</h2>
            <p>
              Mi nombre es , soy una persona que desde pequeño me he sentido atraído
              por el mundo de las TICs. Especialemente, el desarrollo de aplicaciones para dispositivos móviles.
              Entre mis objetivos está en desarrollarme como <span>Desarrollador Mobile</span>
            </p>
          </div>
          <div className="hobbies">
            <ContentHobbies/>
            <ContentHobbies/>
            <ContentHobbies/>
          </div>
        </Col>
      </Row>
    </Container>
    </>
  );
};
const ContentHobbies = ({ icon, detail }) => {
  return (
    <>
      <div className="hobby-item m-2">
              <div className="hobby-icon">⚽</div>
              <div className="hobby-text">Fútbol</div>
            </div>
    </>
  );
};
export default AboutMe;
