import React, { useState } from "react";
import { Container, Row, Col, ProgressBar, Modal, Button } from "react-bootstrap";

const Skills = () => {
  const developmentSkills = [
    <ProgressBarData tittle="HTML" per={80} color="info" />,
    <ProgressBarData tittle="CSS" per={80} color="info" />,
    <ProgressBarData tittle="SCSS" per={80} color="info" />,
    <ProgressBarData tittle="JAVASCRIPT" per={80} color="info" />,
    <ProgressBarData tittle="PHP" per={80} color="info" />
  ];

  // ... define other skill categories in a similar way

  return (
    <>
      <Container>
        <Row className="mt-5 neon-border m-2">
          <Col xs={12} lg={6}>
            <h2>Desarrollo Web</h2>
            {developmentSkills.map((skill, index) => (
              <div key={index}>{skill}</div>
            ))}
            <ButtonModal content={developmentSkills} />
          </Col>
          {/* ... */}
        </Row>
        {/* ... */}
      </Container>
    </>
  );
};

const ProgressBarData = ({ tittle, per, color }) => {
  return (
    <div className="p-2">
      {/* ... */}
    </div>
  );
};

const ButtonModal = ({ content }) => {
  const [showModal, setShowModal] = useState(false);

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  return (
    <>
      <Button
        size="sm"
        variant="outline-primary"
        onClick={handleOpenModal}
      >
        Abrir Modal
      </Button>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        dialogClassName="custom-modal modal-90w"
        aria-labelledby="example-modal-sizes-title-lg"
        centered
        size="xl"
      >
        <Modal.Header>
          <Modal.Title>Contenido</Modal.Title>
          <button className="custom-close-button" onClick={handleCloseModal}>
            &times;
          </button>
        </Modal.Header>

        <Modal.Body>
          {content}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default Skills;
