import { Container, Button, Image, Col, Row } from "react-bootstrap";
import Vivus from "vivus"; // Importamos la biblioteca Vivus
import React, { useEffect, useRef } from "react";
import logoSvg from "../../svg/mobile-app.svg"; // Importamos nuestro archivo SVG (ajusta la ruta según tu estructura)


const Banner = () => {
  const svgRef = useRef(null); // Creamos una referencia para el contenedor del SVG

  useEffect(() => {
    // Usamos el hook useEffect para ejecutar código después del renderizado
    const vivus = new Vivus(svgRef.current, {
      type: "sync", // Tipo de animación (sincrónica)
      duration: 200, // Duración de la animación en milisegundos
    });
  }, []); // La matriz vacía indica que este efecto se ejecuta solo después del montaje inicial

  return (
    <>
      <Container className="p-5">
        <Row className="justify-content-md-center" xs={1} md={1} sm={1} lg={2}>
          <Col className="d-flex flex-column justify-content-center align-items-center">
            <div className="text-center text-sm-start">
              <h1 className="title">JC</h1>
              <p className="profession">
              Soy un <span>Ingeniero de Software</span> apasionado por la programación y y la creación de soluciones innovadoras es el motor que impulsa cada línea que escribo.
              {/*Mantengo mi mente abierta a las nuevas tecnologías, metodologías y tendencias, 
              siempre listo para adoptar aquellas que puedan elevar mi trabajo al siguiente nivel.*/}
              </p>
              <div className="icons my-3">
                <a href="https://github.com/JCAc10" target="_blank" rel="noopener noreferrer">
                <i className="fa-brands fa-github fa-2x p-3"></i>
                </a>
                <a href="https://www.linkedin.com/in/jeanzamudiolabán" target="_blank" rel="noopener noreferrer">
                <i className="fa-brands fa-linkedin fa-2x p-3"></i>
                </a>
                <a href="mailto:jeanzamudio29@gmail.com" target="_blank" rel="noopener noreferrer">
                <i className="fa-brands fa-google fa-2x p-3"></i>
                </a>
              </div>
              <Button variant="outline-info" onClick={handleDownloadPDF}>Descargar CV</Button>
            </div>
          </Col>
          <Col className="d-flex justify-content-center align-items-center">
          <div ref={svgRef} className="p-2" dangerouslySetInnerHTML={{ __html: logoSvg }} />
          </Col>
        </Row>
      </Container>
    </>
  );
};
const handleDownloadPDF = () => {
  const link = document.createElement("a");
  link.href = "../../PDF/CV-.pdf"; // Cambia esto por la ruta correcta de tu archivo PDF
  link.download = "CV-J.pdf"; // Cambia esto por el nombre que quieras para el archivo descargado
  link.target = "_blank";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};
export default Banner;
