import { Container, Button, Image, Col, Row } from "react-bootstrap";

const Banner = () => {
  return (
<>
  <Container>
    <Row className="justify-content-md-center" xs={1} md={1} sm={1} lg={2}>
      <Col sm={8} xs="12" className="d-flex flex-column justify-content-start align-items-start">
        <h1 className="">JEAN CARLOS ZAMUDIO LABÁN</h1>
        <p className="">Soy un Ingeniero de Software y me considero un aficionado por la tecnología y 
          siempre estoy a la vanguardia con el surgimiento de nuevas tecnologías.</p>
        <Button variant="outline-info">Download CV</Button>
      </Col>
      <Col sm={4} className="d-flex justify-content-center align-items-center">
        <Image
          src="https://avatars.githubusercontent.com/u/133301187?v=4"
          roundedCircle
          width={500}
          height={500}
        />
      </Col>
    </Row>
  </Container>
</>

  );
};

export default Banner;
