import React, { useState } from "react";
import { Container, Row, Col, ProgressBar, Modal, Button } from "react-bootstrap";

const Skills = () => {
  return (
    <>
      <Container>
        <ButtonModal
          title="Hola"
          columna1={<Columna1 />}
          columna2={<Columna2 />}
        />
        {/* Resto del contenido */}
      </Container>
    </>
  );
};

const Columna1 = () => {
  return (
    <div>
      {/* Contenido de la primera columna */}
    </div>
  );
};

const Columna2 = () => {
  return (
    <div>
      {/* Contenido de la segunda columna */}
    </div>
  );
};

const ButtonModal = ({ title, columna1, columna2 }) => {
  const [showModal, setShowModal] = useState(false);

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  return (
    <>
      <Button
        size="lg"
        variant="outline-warning"
        onClick={handleOpenModal}
      >
        {title}
      </Button>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        dialogClassName="custom-modal modal-90w"
        aria-labelledby="example-modal-sizes-title-lg"
        centered
        size="xl"
      >
        <Modal.Header>
          <Modal.Title>{title}</Modal.Title>
          <button className="custom-close-button" onClick={handleCloseModal}>
            &times;
          </button>
        </Modal.Header>

        <Modal.Body>
          <Container>
            <Row>
              <Col xs={12} md={6}>
                {columna1}
              </Col>
              <Col xs={12} md={6}>
                {columna2}
              </Col>
            </Row>
          </Container>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default Skills;
