import React, { useState } from "react";
import Img1 from "../../img/fondo1.jpg";
import { Container, Row, Col, Card, Modal } from "react-bootstrap";

const VideoModal = ({ show, onHide, videoId }) => {
  return (
    <Modal show={show} onHide={onHide}  centered
        backdrop="static"
        dialogClassName="custom-modal modal-90w"
        aria-labelledby="example-modal-sizes-title-lg"
        size="xl">
      <Modal.Header closeButton>
        <Modal.Title>Video Reproductor</Modal.Title>
        <button className="custom-close-button" onClick={onHide}>
            &times;
          </button>
      </Modal.Header>
      <Modal.Body>
        <div className="embed-responsive embed-responsive-16by9">
          <iframe
            className="embed-responsive-item"
            src={`https://www.youtube.com/embed/${videoId}`}
            title="YouTube Video Player"
            frameBorder="0"
            allowFullScreen
          ></iframe>
        </div>
      </Modal.Body>
    </Modal>
  );
};

const ProjectsContent = () => {
  const [modalShow, setModalShow] = useState(false);
  const [selectedVideoId, setSelectedVideoId] = useState("");

  const openModal = (videoId) => {
    setSelectedVideoId(videoId);
    setModalShow(true);
  };

  const closeModal = () => {
    setModalShow(false);
    setSelectedVideoId("");
  };

  const renderCard = (title, text, imageUrl, cardKey, videoId) => {
    return (
      <Col key={cardKey} xs={12} lg={6} className="p-2">
        <Card className="bg-dark text-white">
          <div className="position-relative">
            <Card.Img src={imageUrl} alt="Card image" />
            <Card.ImgOverlay>
              <div className="card-content">
                <Card.Title>{title}</Card.Title>
                <Card.Text>{text}</Card.Text>
                <div className="icons my-3">
                  <span
                    className="link-icon"
                    onClick={() => openModal(videoId)}
                  >
                    <i className="fa-solid fa-eye fa-2x px-3"></i>
                  </span>
                  <a href="link-de-github" target="_blank" rel="noopener noreferrer">
                    <i className="fa-solid fa-folder-open fa-2x px-3"></i>
                  </a>
                </div>
              </div>
            </Card.ImgOverlay>
          </div>
        </Card>
      </Col>
    );
  };

  return (
    <>
      <div className="title-subtititel my-3">
        <h2>Mis proyectos</h2>
      </div>
      <Container className="p-4 my-4">
        <Row>
          {renderCard(
            "SISTEMA WEB - FASTAGE",
            "Realizado usando Java Netbeans 8.2 y MySQL, simula un sistema de ventas básico con facturas en PDF",
            Img1,
            1,
            "cuIk2U_bYTU"
          )}
          {/* Agrega más tarjetas aquí */}
        </Row>
      </Container>
      <VideoModal
        show={modalShow}
        onHide={closeModal}
        videoId={selectedVideoId}
      />
    </>
  );
};

export default ProjectsContent;
