import { useState } from 'react';

const Header = () => {
    const [activeItem, setActiveItem] = useState(0);

    const handleItemClick = (index) => {
      setActiveItem(index);
    };
  return (
    <>
 <nav className="sidebar-navigation">
      
      <ul className="p-0">
        <li>
        <img
        src="https://as01.epimg.net/diarioas/imagenes/2022/04/20/actualidad/1650466413_240889_1650466661_noticia_normal_recorte1.jpg"
        alt="Foto de perfil"
        className="profile-image"
      />
        </li>
        <li
          className={activeItem === 0 ? 'active' : ''}
          onClick={() => handleItemClick(0)}
        >
          <ContentList icon="fa-solid fa-user" detail="ABOUT ME"/>
        </li>
        <li
          className={activeItem === 1 ? 'active' : ''}
          onClick={() => handleItemClick(1)}
        >
          <ContentList icon="fa-solid fa-laptop-code" detail="SKILLS"/>
        </li>
        <li
          className={activeItem === 2 ? 'active' : ''}
          onClick={() => handleItemClick(2)}
        >
         <ContentList icon="fa-solid fa-heart" detail="HOBBIES"/>
        </li>
        <li
          className={activeItem === 3 ? 'active' : ''}
          onClick={() => handleItemClick(3)}
        >
         <ContentList icon="fa-solid fa-laptop-code" detail="SKILLS"/>
        </li>
        <li
          className={activeItem === 4 ? 'active' : ''}
          onClick={() => handleItemClick(4)}
        >
         <ContentList icon="fa-solid fa-laptop-code" detail="SKILLS"/>
        </li>
      </ul>
    </nav>


    </>
  )
}
const ContentList = ({ icon, detail }) => {
    return (
      <>
        <i className={icon}></i>
          <div className='info-icon text-center'>{detail}</div>
          <span className="tooltip">{detail}</span>
      </>
    );
  };
export default Header
