import React, { useState } from "react";
import { Nav, Image, Container, Navbar } from "react-bootstrap";

const NavbarHeader = () => {
  const [activeItem, setActiveItem] = useState(0);
  const [showNav, setShowNav] = useState(true);

  const handleItemClick = (index) => {
    setActiveItem(index);
  };

  const toggleNav = () => {
    setShowNav(!showNav);
  };

  return (
    <Navbar expand="lg" data-bs-theme="dark">
      <Container>
        <Navbar.Brand>
          <Image
            src="https://avatars.githubusercontent.com/u/133301187?v=4"
            roundedCircle
            width={50}
            height={50}
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          {/* AÃ±adir la clase "custom-nav" al contenedor Nav */}
          <Nav className="mx-auto custom-nav">
           <Nav.Link>
              <div
                className={activeItem === 0 ? "active" : ""}
                onClick={() => handleItemClick(0)}
              >
                <ContentList icon="fa-solid fa-user" detail="ABOUT ME" isActive={activeItem === 0} />
              </div>
            </Nav.Link>
            <Nav.Link>
              <div
                className={activeItem === 1 ? "active" : ""}
                onClick={() => handleItemClick(1)}
              >
                <ContentList icon="fa-solid fa-code" detail="SKILLS" />
              </div>
            </Nav.Link>
            <Nav.Link>
              <div
                className={activeItem === 2 ? "active" : ""}
                onClick={() => handleItemClick(2)}
              >
                <ContentList icon="fa-solid fa-heart" detail="HOBBIES" />
              </div>
            </Nav.Link>
            <Nav.Link>
              <div
                className={activeItem === 3 ? "active" : ""}
                onClick={() => handleItemClick(3)}
              >
                <ContentList
                  icon="fa-solid fa-graduation-cap"
                  detail="EDUCATION"
                />
              </div>
            </Nav.Link>
            <Nav.Link>
              <div
                className={activeItem === 4 ? "active" : ""}
                onClick={() => handleItemClick(4)}
              >
                <ContentList icon="fa-solid fa-laptop-code" detail="PROJECTS" />
              </div>
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

const ContentList = ({ icon, detail }) => {
  return (
    <>
      <i className={icon}></i>{" "}
      <span className={`info-icon text-center ${isActive ? "active" : ""}`}>{detail}</span>
    </>
  );
};

export default NavbarHeader;
