import { Button, Navbar, Container, Nav } from 'react-bootstrap';
import { useState } from 'react';

const Header = () => {
    const [activeItem, setActiveItem] = useState(0);

    const handleItemClick = (index) => {
      setActiveItem(index);
    };
  return (
    <>
 <nav className="sidebar-navigation">
      
      <ul className="p-0">
        <li>
        <img
        src="https://as01.epimg.net/diarioas/imagenes/2022/04/20/actualidad/1650466413_240889_1650466661_noticia_normal_recorte1.jpg"
        alt="Foto de perfil"
        className="profile-image"
      />
        </li>
        <li
          className={activeItem === 0 ? 'active' : ''}
          onClick={() => handleItemClick(0)}
        >
          <i className="fa-solid fa-user"></i>
          <div>About me</div>
          <span className="tooltip">Sobre m√≠</span>
        </li>
        <li
          className={activeItem === 1 ? 'active' : ''}
          onClick={() => handleItemClick(1)}
        >
          <i className="fa-solid fa-laptop-code"></i>
          <span className="tooltip">Skills</span>
        </li>
        <li
          className={activeItem === 2 ? 'active' : ''}
          onClick={() => handleItemClick(2)}
        >
          <i className="fab fa-facebook"></i>
          <span className="tooltip">Contacts</span>
        </li>
        <li
          className={activeItem === 3 ? 'active' : ''}
          onClick={() => handleItemClick(3)}
        >
          <i className="fa fa-print"></i>
          <span className="tooltip">Fax</span>
        </li>
        <li
          className={activeItem === 4 ? 'active' : ''}
          onClick={() => handleItemClick(4)}
        >
          <i className="fa fa-sliders"></i>
          <span className="tooltip">Settings</span>
        </li>
      </ul>
    </nav>


    </>
  )
}

export default Header
