import React, { useState } from "react";
import { Container, Row, Col, ProgressBar, Modal, Button } from "react-bootstrap";

const Skills = () => {
  return (
    <>
      <Container>
        <Row className="mt-5 neon-border m-2">
          <Col xs={12} lg={6}>
            <h2>Desarrollo Web</h2>
            <ProgressBarData tittle="HTML" per={80} color="info" />
            {/* Agregar bot√≥n para abrir el modal */}
            <ButtonModal content={<ProgressBarData tittle="HTML" per={80} color="info" />} />
            {/* Resto del contenido de la columna */}
          </Col>
          {/* Repetir para las otras columnas */}
        </Row>
        {/* Repetir para las otras filas */}
      </Container>
    </>
  );
};

const ProgressBarData = ({ tittle, per, color }) => {
  return (
    <div className="p-2">
      <div className="text-start">{tittle}:</div>
      <div className="text-end">
        <i className="float-right">{per}%</i>
      </div>
      <ProgressBar now={per} label={`${per}%`} visuallyHidden variant={color} />
    </div>
  );
};

const ButtonModal = ({ content }) => {
  const [showModal, setShowModal] = useState(false);

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  return (
    <>
      <Button
        size="sm"
        variant="outline-primary"
        onClick={handleOpenModal}
      >
        Abrir Modal
      </Button>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        dialogClassName="custom-modal modal-90w"
        aria-labelledby="example-modal-sizes-title-lg"
        centered
        size="xl"
      >
        <Modal.Header>
          <Modal.Title>Contenido</Modal.Title>
          <button className="custom-close-button" onClick={handleCloseModal}>
            &times;
          </button>
        </Modal.Header>

        <Modal.Body>
          {content}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default Skills;
