import React, { useEffect } from 'react';
import './Timeline.css'; // Importa tus estilos personalizados aquí

const TimelineItem = ({ content }) => {
  return <div className="content">{content}</div>;
};

const Timeline = () => {
  useEffect(() => {
    const items = document.querySelectorAll('.content');
    const handleScroll = () => {
      items.forEach((item) => {
        if (item.getBoundingClientRect().top < window.innerHeight) {
          item.classList.add('visible');
        }
      });
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="timeline">
      <div className="parent">
        {/* Repite este bloque de código para cada elemento de la línea de tiempo */}
        <div className="child">
          <TimelineItem content="sequi molestias sed dolore iusto at eaque quas voluptas nobis. Rem adipisci ullam quia unde?" />
        </div>
        {/* Agrega más elementos aquí */}
      </div>
    </div>
  );
};

export default Timeline;
