import { Container, Button, Image, Col, Row } from "react-bootstrap";

const Banner = () => {
  return (
    <>
    <Container>
      <Row className="justify-content-md-center">
      <Col xs={{ order: 'last' }} lg="2">
   
          <h1 className="title">JEAN CARLOS ZAMUDIO LABÁN</h1>
          <p className="profession">Soy un Ingeniero de Software y me considero un aficionado por la tecnología y 
          siempre estoy a la vanguardia con el surgiemiento de nuevas tecnologías.</p>
        <Button variant="outline-info">Download CV</Button>

      </Col>
      <Col xs={{ order: 'first' }} lg="2">
      <Image
            src="https://avatars.githubusercontent.com/u/133301187?v=4"
            roundedCircle
          />

      </Col>
      </Row>
    
    </Container>
    </>
  );
};

export default Banner;
