import { Container, Button, Col, Row } from "react-bootstrap";
import React from "react";
import Vivus from "vivus"; // Asegúrate de importar la biblioteca Vivus

import "vivus/dist/vivus.min.js"; // Asegúrate de importar el archivo minificado de Vivus

const Banner = () => {
  const svgRef = React.useRef(null); // Referencia para el elemento SVG

  React.useEffect(() => {
    // Inicializar Vivus con el tipo de animación 'oneByOne'
    new Vivus(svgRef.current, { duration: 200, type: "oneByOne" });
  }, []);

  return (
    <>
      <Container className="p-5">
        <Row
          className="justify-content-md-center"
          xs={1}
          md={1}
          sm={1}
          lg={2}
        >
          <Col className="d-flex flex-column justify-content-center align-items-center">
            <div className="text-center text-sm-start">
              <h1 className="title">JC</h1>
              <p className="profession">
                Soy un <span>Ingeniero de Software</span> apasionado por la
                programación y la creación de soluciones innovadoras es el
                motor que impulsa cada línea que escribo.
                {/*Mantengo mi mente abierta a las nuevas tecnologías, metodologías y tendencias, 
              siempre listo para adoptar aquellas que puedan elevar mi trabajo al siguiente nivel.*/}
              </p>
              <div className="icons my-3">
                <a
                  href="https://github.com/JCAc10"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <i className="fa-brands fa-github fa-2x p-3"></i>
                </a>
                <a
                  href="https://www.linkedin.com/in/jeanzamudiolabán"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <i className="fa-brands fa-linkedin fa-2x p-3"></i>
                </a>
                <a
                  href="mailto:jeanzamudio29@gmail.com"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <i className="fa-brands fa-google fa-2x p-3"></i>
                </a>
              </div>
              <Button variant="outline-info" onClick={handleDownloadPDF}>
                Descargar CV
              </Button>
            </div>
          </Col>
          <Col className="d-flex justify-content-center align-items-center">
            <svg
              ref={"../../svg/mobile-app.svg"}
              width="400"
              height="400"
              xmlns="../../svg/mobile-app.svg"
              viewBox="0 0 400 400"
            >
              <circle cx="200" cy="200" r="150" fill="#ccc" />
            </svg>
          </Col>
        </Row>
      </Container>
    </>
  );
};

const handleDownloadPDF = () => {
  const link = document.createElement("a");
  link.href = "../../PDF/CV-.pdf"; // Cambia esto por la ruta correcta de tu archivo PDF
  link.download = "CV-J.pdf"; // Cambia esto por el nombre que quieras para el archivo descargado
  link.target = "_blank";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

export default Banner;
