import React from 'react';

const CertificationItem = ({ date, certification }) => {
  return (
    <div className="certification-item">
      <div className="certification-date">{date}</div>
      <div className="certification-name">{certification}</div>
    </div>
  );
};

const CertificationContent = () => {
  const certificationData = [
    { date: 'Enero 2020', certification: 'Certificado de Desarrollo Web' },
    { date: 'Abril 2021', certification: 'Certificado de Diseño Gráfico' },
    // Agrega más certificaciones aquí
  ];

  return (
    <div className="certification-timeline">
      {certificationData.map((item, index) => (
        <div className="timeline-item" key={index}>
          <div className="timeline-circle"></div>
          <CertificationItem
            date={item.date}
            certification={item.certification}
          />
        </div>
      ))}
    </div>
  );
};

export default CertificationContent;
