import React, { useState } from "react";
import img1 from "../../img/Fastage-Fondo.jpg"
import { Container, Row, Col, Modal } from "react-bootstrap";

const VideoModal = ({ show, onHide, videoId }) => {
  return (
    <Modal show={show} onHide={onHide} size="lg" centered>
      <Modal.Header closeButton>
        <Modal.Title>Video Reproductor</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="embed-responsive embed-responsive-16by9">
          <iframe
            className="embed-responsive-item"
            src={`https://www.youtube.com/embed/${videoId}`}
            title="YouTube Video Player"
            frameBorder="0"
            allowFullScreen
          ></iframe>
        </div>
      </Modal.Body>
    </Modal>
  );
};

const ProjectsContent = () => {
  const [modalShow, setModalShow] = useState(false);
  const [selectedVideoId, setSelectedVideoId] = useState("");

  const openModal = (videoId) => {
    setSelectedVideoId(videoId);
    setModalShow(true);
  };

  const closeModal = () => {
    setModalShow(false);
    setSelectedVideoId("");
  };

  return (
    <>
      <div className="title-subtititel my-3">
        <h2>Mis proyectos</h2>
      </div>
      <Container className="p-4 my-4">
        <Row>
          <Col>
            <div
              className="video-thumbnail"
              onClick={() => openModal("cuIk2U_bYTU")}
            >
              {/* Aquí podrías usar una imagen en miniatura del video */}
              <img
                src={img1}
                alt="Video Thumbnail"
                width={180}
                height={120}
              />
            </div>
          </Col>
          {/* Agrega más columnas aquí si es necesario */}
        </Row>
      </Container>
      <VideoModal
        show={modalShow}
        onHide={closeModal}
        videoId={selectedVideoId}
      />
    </>
  );
};

export default ProjectsContent;
