import React, { useState } from "react";
import { Container, Row, Col, ProgressBar, Modal, Button } from "react-bootstrap";

const Skills = () => {
  return (
    <>
      <Container>
        <ButtonModal title="Hola" content={<SkillsContent />} />
        {/* Resto del contenido */}
      </Container>
    </>
  );
};

const SkillsContent = () => {
  return (
    <>
      <Row className="mt-5 neon-border m-2">
        {/* Contenido de las habilidades */}
      </Row>
      <Row className="mt-5 neon-border-r m-2">
        {/* Contenido de las habilidades */}
      </Row>
      <Row className="mt-5 neon-border-g m-2">
        {/* Contenido de las habilidades */}
      </Row>
    </>
  );
};

const ProgressBarData = ({ tittle, per, color }) => {
  return (
    <div className="p-2">
      <div className="text-start">{tittle}:</div>
      <div className="text-end">
        <i className="float-right">{per}%</i>
      </div>
      <ProgressBar now={per} label={`${per}%`} visuallyHidden variant={color} />
    </div>
  );
};

const ButtonModal = ({ title, content }) => {
  const [showModal, setShowModal] = useState(false);

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  return (
    <>
      <Button
        size="lg"
        variant="outline-warning"
        onClick={handleOpenModal}
      >
        {title}
      </Button>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        dialogClassName="custom-modal modal-90w"
        aria-labelledby="example-modal-sizes-title-lg"
        centered
        size="xl"
      >
        <Modal.Header>
          <Modal.Title>{title}</Modal.Title>
          <button className="custom-close-button" onClick={handleCloseModal}>
            &times;
          </button>
        </Modal.Header>

        <Modal.Body>
          {content && <Container>{content}</Container>}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default Skills;
