import React, { useState } from "react";
import { Container, Row, Col, Button, Modal } from "react-bootstrap";

const Timeline = () => {
  return (
    <Container>
      <Row className="timeline-container">
        <Col xs={12} lg={6}>
          <h2>EDUCACIÓN</h2>
          <div className="timeline">
            <TimeLineItem
              year="2019 - En curso (Egreso aproximado 2023)"
              title="UNIVERSIDAD TECNOLÓGICA DEL PERÚ (UTP)"
              desc="Actualmente me encuentro cursando la carrea de Ingeniería de Software, he aprendido lenguajes como JAVA, PYTHON y C#.
              Además, llevo cursos sobre desarrollo de juegos en Unity 2D, ciberseguridad y redes"
            />
            {/* ... */}
          </div>
        </Col>
        <Col xs={12} lg={6}>
          <h2>CURSOS - SEMINARIOS</h2>
          <div className="timeline">
            <TimeLineItem year="2007" title="ORACLE" desc="adsada" />
            {/* ... */}
          </div>
        </Col>
      </Row>
    </Container>
  );
};

const TimeLineItem = ({ year, title, desc }) => {
  const [showModal, setShowModal] = useState(false);

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  return (
    <>
      <div className="timeline-item">
        <div className="timeline-dot"></div>
        <div className="timeline-content">
          <div className="timeline-balloon">
            <h3 className="m-1">{title}</h3>
            <p>{desc}</p>
            <div className="timeline-year">{year}</div>
            <Button variant="primary" className="my-2" onClick={handleOpenModal}>
              Descargar
            </Button>
          </div>
        </div>
      </div>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>{desc}</p>
          <div className="timeline-year">{year}</div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cerrar
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default Timeline;
