import Vivus from "vivus";
import "vivus/dist/vivus.min.js";
import svgImg from "../../svg/mobile-app.svg"; // Ajusta la ruta a tu archivo SVG
import { Container, Button, Col, Row } from "react-bootstrap";

const Banner = () => {
  const svgRef = useRef(null);

