import React, { useState } from "react";
import { Container, Row, Col, Button, Modal } from "react-bootstrap";

const Timeline = () => {
  return (
    <Container>
      <Row className="timeline-container">
        <Col xs={12} lg={6} >
          <ButtonModal 
            title="EDUCACIÓN"
            columna1={[
              {
                year: "2019 - En curso (Egreso aproximado 2023)",
                title: "UNIVERSIDAD TECNOLÓGICA DEL PERÚ (UTP)",
                desc: "Actualmente me encuentro cursando la carrea de Ingeniería de Software, he aprendido lenguajes como JAVA, PYTHON y C#. Además, llevo cursos sobre desarrollo de juegos en Unity 2D, ciberseguridad y redes",
              },
              {
                year: "2007",
                title: "CISCO NETWORKING ACADEMY",
                desc: "adsada",
              },
            ]}
            columna2={[
              {
                year: "2010",
                title: "PYTHON INSTITUTE",
                desc: "adsada",
              },
            ]}
          />
        </Col>
        <Col xs={12} lg={6}>
          <ButtonModal
            title="CURSOS - SEMINARIOS"
            columna1={[
              {
                year: "2007",
                title: "ORACLE",
                desc: "adsada",
              },
              {
                year: "2010",
                title: "EXCEL UTP",
                desc: "adsada",
              },
            ]}
            columna2={[
              {
                year: "2010",
                title: "APP UTP",
                desc: "adsada",
              },
              {
                year: "2010",
                title: "INGLES UTP",
                desc: "adsada",
              },
            ]}
          />
        </Col>
      </Row>
    </Container>
  );
};

const ButtonModal = ({ title, columna1, columna2 }) => {
  const [showModal, setShowModal] = useState(false);
  const [selectedContent, setSelectedContent] = useState(null);
  const shouldUseTwoColumns = (columna1 && columna1.length > 2) || (columna2 && columna2.length > 2);

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleOpenModal = (content) => {
    setSelectedContent(content);
    setShowModal(true);
  };

  return (
    <>
      <h2>{title}</h2>
      <Button variant="primary" onClick={() => handleOpenModal(columna1.concat(columna2))}>
        Ver detalles
      </Button>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        dialogClassName={`custom-modal${shouldUseTwoColumns ? " two-columns" : ""}`}
        aria-labelledby="contained-modal-title-vcenter"
        centered
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedContent && (
            <Container>
              <Row>
                {shouldUseTwoColumns ? (
                  <>
                    <Col xs={12} md={6}>
                    <div className="timeline">
      {selectedContent.map((item, index) => (
        <div key={index} className="timeline-item">
          <div className="timeline-dot"></div>
          <div className="timeline-content">
            <div className="timeline-balloon">
              <h3 className="m-1">{item.title}</h3>
              <p>{item.desc}</p>
              <div className="timeline-year">{item.year}</div>
              <a href="#" className="btn btn-primary my-2">
                Descargar
              </a>
            </div>
          </div>
        </div>
      ))}
    </div>
                    </Col>
                    <Col xs={12} md={6}>
                    <div className="timeline">
      {selectedContent.map((item, index) => (
        <div key={index} className="timeline-item">
          <div className="timeline-dot"></div>
          <div className="timeline-content">
            <div className="timeline-balloon">
              <h3 className="m-1">{item.title}</h3>
              <p>{item.desc}</p>
              <div className="timeline-year">{item.year}</div>
              <a href="#" className="btn btn-primary my-2">
                Descargar
              </a>
            </div>
          </div>
        </div>
      ))}
    </div>
                    </Col>
                  </>
                ) : (
                  <Col>
                    <div className="timeline">
                      {selectedContent.map((item, index) => (
                        <div key={index} className="timeline-item">
                          {/* ... Rest of your timeline item code ... */}
                        </div>
                      ))}
                    </div>
                  </Col>
                )}
              </Row>
            </Container>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cerrar
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default Timeline;
