import React, { useState } from "react";
import pdfData from "../../data/pdfData";
import { Container, Row, Col, Button, Modal, Card } from "react-bootstrap";

const Timeline = () => {
  return (
    <>
      <div className="title-subtititel my-3">
        <h2>Resumen de estudios</h2>
      </div>
      <Container className="p-4 my-4">
        <Row className="timeline-container justify-content-center">
          <Col xs={12} lg={6} className="p-2">
            <CustomCard
              title="EDUCACIÃ“N"
              icon="fa-solid fa-user-graduate"
              content={pdfData.educacion}
            />
          </Col>
          <Col xs={12} lg={6} className="p-2">
            <CustomCard
              title="CURSOS - SEMINARIOS"
              icon="fa-solid fa-award"
              content={pdfData.cursos}
            />
          </Col>
        </Row>
      </Container>
    </>
  );
};

const CustomCard = ({ title, icon, content }) => {
  const [showModal, setShowModal] = useState(false);
  const [selectedContent, setSelectedContent] = useState(null);

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleOpenModal = (content) => {
    setSelectedContent(content);
    setShowModal(true);
  };

  return (
    <>
      <Button
        size="lg"
        variant="outline-light"
        onClick={() => handleOpenModal(content)}
      >
        {title} <i className={icon}></i>
      </Button>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        backdrop="static"
        dialogClassName="custom-modal modal-90w"
        aria-labelledby="example-modal-sizes-title-lg"
        centered
        size="xl"
      >
        <Modal.Header>
          <Modal.Title>{title}</Modal.Title>
          <button className="custom-close-button" onClick={handleCloseModal}>
            &times;
          </button>
        </Modal.Header>

        <Modal.Body>
          {selectedContent && (
            <Container>
              <Row>
                {selectedContent.map((item, index) => (
                  <Col key={index} xs={12} md={6}>
                    <div className="timeline">
                      <div className="timeline-item">
                        <div className="timeline-dot"></div>
                        <div className="timeline-content">
                          <div className="timeline-balloon">
                            <h3 className="">{item.title}</h3>
                            <p>{item.desc}</p>
                            <div className="timeline-year">{item.year}</div>
                            <a
                              href={item.downloadUrl}
                              className="btn btn-outline-info my-2"
                              target="_blank"
                              rel="noopener noreferrer"
                            >
                              Descargar
                            </a>
                          </div>
                        </div>
                      </div>
                    </div>
                  </Col>
                ))}
              </Row>
            </Container>
          )}
        </Modal.Body>
      </Modal>
    </>
  );
};

export default Timeline;
