import React, { useState } from "react";
import { Container, Button, Image, Col, Row } from "react-bootstrap";
import { useSpring, animated } from "react-spring"; // Importa la librería react-spring
import svgImg from "../../svg/mobile-app.svg";

const Banner = () => {
  const [isFlipped, setIsFlipped] = useState(false);

  const { transform, opacity } = useSpring({
    opacity: isFlipped ? 1 : 0,
    transform: `perspective(600px) rotateX(${isFlipped ? 180 : 0}deg)`,
    config: { mass: 5, tension: 500, friction: 80 }
  });

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <>
      <Container className="p-5">
        <Row className="justify-content-md-center" xs={1} md={1} sm={1} lg={2}>
          <Col className="d-flex flex-column justify-content-center align-items-center">
            {/* Resto de tu contenido aquí */}
          </Col>
          <Col className="d-flex justify-content-center align-items-center">
            <div onClick={handleFlip}>
              <animated.div
                style={{
                  opacity: opacity.interpolate(o => 1 - o),
                  transform
                }}
              >
                <Image className="p-2" src={svgImg} roundedCircle fluid width={400} height={400} />
              </animated.div>
              <animated.div
                style={{
                  opacity,
                  transform: transform.interpolate(t => `${t} rotateX(180deg)`)
                }}
              >
                {/* Aquí puedes agregar el contenido que se mostrará en el reverso */}
                <p>Contenido de la parte trasera del teléfono</p>
              </animated.div>
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Banner;
