import React from "react";
import { Container, Row, Col } from "react-bootstrap";

const Timeline = () => {
  return (
    <Container>
      <Row className="timeline-container">
        <Col xs={12} lg={6}>
          <div className="timeline">
          <h2>Frameworks</h2>
            <TimeLineItem year="2002" title="a" desc="adsada" state="En curso" />
            <TimeLineItem year="2007" title="a" desc="adsada" state="Concluso" />
            <TimeLineItem year="2010" title="a" desc="adsada" state="En curso" />
          </div>
        </Col>
        <Col xs={12} lg={6}>
          <div className="timeline">
            <TimeLineItem year="2015" title="a" desc="adsada" state="Concluso" />
            <TimeLineItem year="2020" title="a" desc="adsada" state="En curso" />
            <TimeLineItem year="2023" title="a" desc="adsada" state="Concluso" />
          </div>
        </Col>
      </Row>
    </Container>
  );
};

const TimeLineItem = ({ year, title, desc, state }) => {
  return (
    <>
      <div className="timeline-item">
        <div className="timeline-dot"></div>
        <div className="timeline-content">
          <div className="timeline-balloon">
            <h3 className="m-1">{title}</h3>
            <p>{desc}</p>
            <div className="timeline-year">{year}</div>
            <div className="timeline-state">{state}</div>
            <a href="#" className="btn btn-primary">
              Descargar
            </a>
          </div>
        </div>
      </div>
    </>
  );
};

export default Timeline;
